<?php	class TypeCompte {		public $id = "";	public $libelle;	public $libelle_long;	public $ordre;				public function __construct($id_type = "") {		if($id_type !=""){			return $this->charger($id_type);		}    }		public function charger($id_type){		$this->id = $id_type;				$q = "SELECT * FROM ".db_table_name('types_compte')." WHERE id = $this->id";				$res = mysql_query($q);    	if (!$res) {    		throw new MyException('Invalid query: '.$q . mysql_error());		}    	$nb_res = mysql_num_rows($res);    	if($nb_res != 1) {    		return false;    	}else{    		$result = mysql_fetch_assoc($res);    		    		$this->libelle = stripslashes($result['libelle']);			$this->libelle_long = stripslashes($result['libelle_long']);			$this->ordre = $result['ordre'];    		return true;    	}	}		public function chargerComptes(){							}		public static function getListe(){			$class_name = get_called_class();				$ma_class = new $class_name();				$q = "SELECT id FROM ".db_table_name('types_compte')." WHERE 1 ORDER BY ordre ASC";		$res = mysql_query($q);		if (!$res) {			throw new MyException('Invalid query: '.$q . mysql_error());		}		$nb_res = mysql_num_rows($res);		if($nb_res == 0) {			return array();		}else{			while($result = mysql_fetch_assoc($res)){				$return[] = new $class_name($result['id']);			}		}		return $return;	}	}